using System.Collections;using Backfire;using UnityEngine;public sealed class EnemyBullet : MonoBehaviour, IParryable{     private GameObject player;     internal Rigidbody2D rb;    public float force;    public bool follow = true;    void Awake()    {        rb = GetComponent<Rigidbody2D>();    }        void Start()    {        if (follow)        {            player = GameObject.FindGameObjectWithTag("Player");            Vector3 direction = player.transform.position - transform.position;            rb = GetComponent<Rigidbody2D>();            rb.linearVelocity = new Vector2(direction.x, direction.y).normalized * force;        }        Destroy(gameObject, 5f);    }    void OnCollisionEnter2D(Collision2D other)    {        if(other.gameObject.CompareTag("Player"))        {            other.gameObject.GetComponent<Controller>()?.TakeDamage();        }        else if (other.gameObject.CompareTag("Bounce"))        {            return;        }        else if (other.gameObject.CompareTag("Environment")) Destroy(gameObject);        else Destroy(gameObject);    }    void OnTriggerEnter2D(Collider2D other)    {        if (other.gameObject.CompareTag("Environment")) Destroy(gameObject);    }    public void Parry(Vector2 newDir, bool isPefect = false)    {        Destroy(gameObject);    }}