using UnityEngine;public abstract class Controller : MonoBehaviour, IDamageable{    // Start is called once before the first execution of Update after the MonoBehaviour is created    [SerializeField] protected float maxHealth;    [SerializeField] public float health;    void Awake()    {        maxHealth = health;    }    // Update is called once per frame    void Update()    {            }    public abstract bool TakeDamage();    public float Health     {         get => health;         set        {            health = Mathf.Clamp(value, 0f, maxHealth);        }    }}