using UnityEngine;using UnityEngine.InputSystem;namespace Backfire{    public sealed class MouseAim : MonoBehaviour    {        public Vector2 AimDirection { get; private set; }        private Camera _camera;        void Awake()        {            _camera = Camera.main;        }        void Update()        {            Vector3 mousePos = Mouse.current.position.ReadValue();            Vector3 worldPos = _camera.ScreenToWorldPoint(mousePos, Camera.MonoOrStereoscopicEye.Mono);            Vector2 playerPos = transform.position;            var delta = worldPos - (Vector3)playerPos;            AimDirection = delta.sqrMagnitude < 0.001f ? AimDirection : delta.normalized;        }    }}