using System.Collections;using Backfire;using UnityEngine;public class PlayerDeath : PlayerState{        public PlayerDeath(PlayerController player) : base(player) {}    [Tooltip("Seconds to wait before respawning")]    [SerializeField] private float respawnDelay = 1f;    public override PlayerStateType StateType => PlayerStateType.Death;    public override void OnEnter()    {        GameManager.Instance.GameIsRunning = false;        player.StartCoroutine(Respawn());    }    private IEnumerator Respawn()    {        //yield return new WaitForSeconds(respawnDelay);        player.SetState(PlayerStateType.PreState);        yield return null;    }    }